mapper.select:
select

,

from coterie_article ORDER BY create_time DESC LIMIT 9;

select
,
from coterie_article where (YEARWEEK(DATE_FORMAT(coterie_article.create_time, '%Y-%m-%d'),0)= YEARWEEK(NOW(), 0)) ORDER BY coterie_article.`tour_num` DESC LIMIT 9;

select

,

from coterie_article where DATE(create_time)=DATE(NOW())
ORDER BY tour_num DESC LIMIT 9;
